    private void btnMostrarActionPerformed(java.awt.event.ActionEvent evt) {
        if (comboFigura.getItemCount() > 0) {
            int indiceFigura = comboFigura.getSelectedIndex();
            int indiceColor = comboColor.getSelectedIndex();

            switch (indiceFigura) {
                //Circulo
                case 0:
                    switch (indiceColor) {
                        //Amarillo
                        case 0:
                             circulo(this.getGraphics(), color.YELLOW);
                            break;
                        //Azul
                        case 1:
                            circulo(this.getGraphics(), color.BLUE);
                            break;
                        //Rojo
                        case 2:
                            circulo(this.getGraphics(), color.RED);
                            break;
                        default:
                            break;
                    }

                    break;
                //Cuadrado
                case 1:
                    switch (indiceColor) {
                        //Amarillo
                        case 0:
                             cuadrado(this.getGraphics(), color.YELLOW);
                            break;
                        //Azul
                        case 1:
                            cuadrado(this.getGraphics(), color.BLUE);
                            break;
                        //Rojo
                        case 2:
                            cuadrado(this.getGraphics(), color.RED);
                            break;
                        default:
                            break;
                    }
                    break;
                //Rectangulo
                case 2:
                    switch (indiceColor) {
                        //Amarillo
                        case 0:
                             triangulo(this.getGraphics(), color.YELLOW);
                            break;
                        //Azul
                        case 1:
                            triangulo(this.getGraphics(), color.BLUE);
                            break;
                        //Rojo
                        case 2:
                            triangulo(this.getGraphics(), color.RED);
                            break;
                        default:
                            break;
                    }
                    break;
                default:
                    break;
            }
        }
    }

