    public void circulo(Graphics g, Color color) {
        super.paint(g);
        g.setColor(color);
        g.fillOval(250, 70, 70, 70);

    }
     public void cuadrado(Graphics g, Color color) {
        super.paint(g);
        g.setColor(color);
        g.fillRect(250, 70, 70, 70);
    }
     public void triangulo(Graphics g, Color color) {
        super.paint(g);
        g.setColor(color);
        int vectorX[] = {300, 350, 250};
        int vectorY[] = {70, 140, 140};
        g.fillPolygon(vectorX, vectorY, 3);
    }
